const utf8 = require('utf8')
const sharp = require('sharp')

exports.handler = async function (event) {
  try {
    let textArray = []
    let line = ''
    const offset = 28
    let text = 'Home of fearless web developer Silvestar BistroviÄ‡'

    if(event.queryStringParameters && event.queryStringParameters.text) {
      text = event.queryStringParameters.text
    }

    let words = text.split(' ')

    for (let i = 0; i < words.length; i++) {
      if(line.length + words[i].length < offset) {
        line.length += words[i].length;
        line += ` ${words[i]}`
      } else {
        textArray.push(line)
        line.length = words[i].length
        line = words[i]
      }

      if (i === words.length - 1) {
        textArray.push(line)
      }
    }

    const yPosition = 320
    const yOffset = 65
    let tspan = ''

    for (let i = 0; i < textArray.length; i++) {
      if(i < 4) {
        tspan += `<tspan x="260" y="${yPosition + (yOffset * i)}">${utf8.encode(textArray[i].trim())}</tspan>`
      }
    }

    const svg = `<svg width="1200" height="630" viewBox="0 0 1200 630" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><rect width="1200" height="630" rx="20" fill="white"/><rect x="56" y="56" width="1088" height="518" rx="15" stroke="#FF3366" stroke-width="14"/><rect x="100" y="100" width="125" height="126" fill="url(#pattern0)"/><defs><pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1"><use xlink:href="#image0_1411_54" transform="scale(0.004 0.00396825)"/></pattern><image id="image0_1411_54" width="250" height="252" xlink:href="data:image/png;base64,"/></defs><style>text{background:gold;font-family:-apple-system,BlinkMacSystemFont,'Segoe UI',Roboto,Helvetica,Arial,sans-serif,'Apple Color Emoji','Segoe UI Emoji','Segoe UI Symbol';font-size:55px;font-weight:bold;fill:#000;stroke:#000;overflow:hidden;}</style><text x="260" y="279" height="247" width="816">${tspan}</text></svg>`

    const img = await sharp(Buffer.from(svg))
      .png()
      .toBuffer()

    return {
      statusCode: 200,
      body: img.toString('base64'),
      headers: {
        "content-type": "image/png"
      },
      isBase64Encoded: true,
    }
  } catch (error) {
    console.log(error)

    return {
      statusCode: 200,
      body: 'Error!',
      headers: {
        "content-type": "text"
      }
    }
  }
}
